import { useEffect, useState, useRef } from 'react';
import { motion } from 'motion/react';
import './EncryptText.css';

const EncryptText = ({
    text,
    speed = 40,
    maxIterations = 12,
    characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*()_+',
    className = 'encrypt-text',
    encryptedClassName = 'encrypted',
    direction = 'ltr', // 'ltr' for left-to-right, 'rtl' for right-to-left
    ...props
}) => {
    const [displayText, setDisplayText] = useState(text);
    const [isHovering, setIsHovering] = useState(false);
    const [isAnimating, setIsAnimating] = useState(false);
    const intervalRef = useRef(null);
    const iterationRef = useRef(0);

    // Helper function to scramble a single character
    const scrambleChar = () => {
        const chars = characters.split('');
        return chars[Math.floor(Math.random() * chars.length)];
    };

    useEffect(() => {
        clearInterval(intervalRef.current);
        iterationRef.current = 0;

        if (isHovering) {
            // Encrypt on hover with directional effect
            setIsAnimating(true);
            const textLength = text.length;

            intervalRef.current = setInterval(() => {
                iterationRef.current++;

                setDisplayText(prev => {
                    return text.split('').map((char, index) => {
                        if (char === ' ') return ' ';

                        // Calculate which characters should be revealed based on direction
                        const revealIndex = direction === 'ltr'
                            ? Math.floor((iterationRef.current / maxIterations) * textLength)
                            : textLength - Math.floor((iterationRef.current / maxIterations) * textLength);

                        // Check if this character should be revealed
                        const shouldReveal = direction === 'ltr'
                            ? index < revealIndex
                            : index >= revealIndex;

                        return shouldReveal ? char : scrambleChar();
                    }).join('');
                });

                if (iterationRef.current >= maxIterations) {
                    setDisplayText(text);
                    clearInterval(intervalRef.current);
                    setIsAnimating(false);
                }
            }, speed);
        } else {
            // Decrypt on mouse leave with reverse directional effect
            setIsAnimating(true);
            const textLength = text.length;
            const decryptIterations = Math.floor(maxIterations / 2);

            intervalRef.current = setInterval(() => {
                iterationRef.current++;

                if (iterationRef.current >= decryptIterations) {
                    setDisplayText(text);
                    clearInterval(intervalRef.current);
                    setIsAnimating(false);
                } else {
                    setDisplayText(prev => {
                        return text.split('').map((char, index) => {
                            if (char === ' ') return ' ';

                            // Reverse the direction for decrypt
                            const revealIndex = direction === 'ltr'
                                ? textLength - Math.floor((iterationRef.current / decryptIterations) * textLength)
                                : Math.floor((iterationRef.current / decryptIterations) * textLength);

                            const shouldReveal = direction === 'ltr'
                                ? index >= revealIndex
                                : index < revealIndex;

                            return shouldReveal ? char : scrambleChar();
                        }).join('');
                    });
                }
            }, speed);
        }

        return () => clearInterval(intervalRef.current);
    }, [isHovering, text, speed, maxIterations, direction, characters]);

    return (
        <motion.div
            className={`wrapper ${className}`}
            onMouseEnter={() => setIsHovering(true)}
            onMouseLeave={() => setIsHovering(false)}
            {...props}
        >
            <span className="srOnly">{text}</span>
            <span aria-hidden="true" className={isAnimating ? encryptedClassName : ''}>
                {displayText.split('').map((char, index) => (
                    <span key={index}>{char}</span>
                ))}
            </span>
        </motion.div>
    );
};

EncryptText.propTypes = {
  text: PropTypes.string.isRequired,
  speed: PropTypes.number,
  maxIterations: PropTypes.number,
  characters: PropTypes.string,
  className: PropTypes.string,
  encryptedClassName: PropTypes.string,
  direction: PropTypes.oneOf(['ltr', 'rtl']),
};

export default EncryptText;
