import { useEffect, useRef, useState } from 'react';
import '././css/EncryptText.css';

const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789@#$%^&*()_+=-<>?{}[]|/';

const EncryptText = ({
  text = 'Hover to Encrypt',
  speed = 30,
  className = "encrypt-text",
  encryptedChars = chars,
  style = {}
}) => {
  const [displayText, setDisplayText] = useState(text);
  const [hovered, setHovered] = useState(false);
  const intervalRef = useRef(null);

  // Encrypt animation on hover
  useEffect(() => {
    if (hovered) {
      let iteration = 0;
      clearInterval(intervalRef.current);
      intervalRef.current = setInterval(() => {
        setDisplayText(prev =>
          text
            .split('')
            .map((_, i) =>
              i < iteration
                ? text[i]
                : encryptedChars[Math.floor(Math.random() * encryptedChars.length)]
            )
            .join('')
        );
        iteration += 1 / 2;
        if (iteration >= text.length) clearInterval(intervalRef.current);
      }, speed);
    } else {
      // Decrypt smoothly back to original
      clearInterval(intervalRef.current);
      let iteration = text.length;
      intervalRef.current = setInterval(() => {
        setDisplayText(prev =>
          text
            .split('')
            .map((_, i) =>
              i < iteration
                ? text[i]
                : encryptedChars[Math.floor(Math.random() * encryptedChars.length)]
            )
            .join('')
        );
        iteration -= 1 / 2;
        if (iteration <= 0) {
          setDisplayText(text);
          clearInterval(intervalRef.current);
        }
      }, speed);
    }

    return () => clearInterval(intervalRef.current);
  }, [hovered, text, encryptedChars, speed]);

  return (
    <span
      className={`encrypt-text ${className}`}
      style={{
        fontFamily: 'monospace',
        display: 'inline-block',
        cursor: 'pointer',
        transition: 'color 0.3s ease',
        ...style
      }}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      {displayText}
    </span>
  );
};

export default EncryptText;
