import { useEffect, useState, useRef } from 'react';
import { motion } from 'motion/react';

const styles = {
  wrapper: {
    display: 'inline-block',
    whiteSpace: 'pre-wrap',
    cursor: 'pointer',
  },
  srOnly: {
    position: 'absolute',
    width: '1px',
    height: '1px',
    padding: 0,
    margin: '-1px',
    overflow: 'hidden',
    clip: 'rect(0,0,0,0)',
    border: 0,
  },
};

export default function HoverEncryptText({
  text,
  speed = 40,
  maxIterations = 12,
  characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*()_+',
  className = '',
  encryptedClassName = 'encrypted',
  ...props
}) {
  const [displayText, setDisplayText] = useState(text);
  const [isHovering, setIsHovering] = useState(false);
  const [isAnimating, setIsAnimating] = useState(false);
  const intervalRef = useRef(null);

  // Helper function to scramble characters
  const scramble = (originalText) => {
    const chars = characters.split('');
    return originalText
      .split('')
      .map((ch) => (ch === ' ' ? ' ' : chars[Math.floor(Math.random() * chars.length)]))
      .join('');
  };

  useEffect(() => {
    clearInterval(intervalRef.current);
    let iterations = 0;

    if (isHovering) {
      // Encrypt on hover
      setIsAnimating(true);
      intervalRef.current = setInterval(() => {
        setDisplayText(scramble(text));
        iterations++;
        if (iterations >= maxIterations) {
          clearInterval(intervalRef.current);
        }
      }, speed);
    } else {
      // Decrypt on mouse leave
      setIsAnimating(true);
      intervalRef.current = setInterval(() => {
        iterations++;
        if (iterations >= maxIterations / 2) {
          setDisplayText(text);
          clearInterval(intervalRef.current);
          setIsAnimating(false);
        } else {
          setDisplayText(scramble(text));
        }
      }, speed);
    }

    return () => clearInterval(intervalRef.current);
  }, [isHovering, text, speed, maxIterations]);

  return (
    <motion.span
      style={styles.wrapper}
      onMouseEnter={() => setIsHovering(true)}
      onMouseLeave={() => setIsHovering(false)}
      className={className}
      {...props}
    >
      <span style={styles.srOnly}>{text}</span>
      <span aria-hidden="true">
        {displayText.split('').map((char, index) => (
          <span
            key={index}
            className={isAnimating && isHovering ? encryptedClassName : className}
            style={{ transition: 'color 0.3s ease' }}
          >
            {char}
          </span>
        ))}
      </span>
    </motion.span>
  );
}
